<span class="line"><span style="color: #ABB2BF">    </span><span style="color: #61AFEF">1</span><span style="color: #ABB2BF">  </span><span style="color: #98C379">echo</span><span style="color: #ABB2BF"> </span><span style="color: #E06C75">$HOME</span></span>
