<span class="line"><span style="color: #C678DD">if</span><span style="color: #ABB2BF"> [ </span><span style="color: #98C379">&quot;</span><span style="color: #E06C75; font-style: italic">$1</span><span style="color: #98C379">&quot;</span><span style="color: #ABB2BF"> </span><span style="color: #56B6C2">==</span><span style="color: #ABB2BF"> </span><span style="color: #98C379">&quot;&quot;</span><span style="color: #ABB2BF"> ]</span></span>
